#include <stdint.h>

#if !defined(__SOFT_FP__) && defined(__ARM_FP)
  #warning "FPU is not initialized, but the project is compiling for an FPU. Please initialize the FPU before use."
#endif

int main(void)
{
	// the base address of RCC: `0x40023800`
	// Address offset: 0x30
	// used to turn on the clock
	uint32_t *pClockControlRegister = (uint32_t*) (0x40023800 + 0x30);
	// the base address of GPIOD mode register: `0x40020C00`
	// Address offset: 0x00
	// used to control the mode of GPIOD
	uint32_t *pGPIODModeRegister = (uint32_t*) (0x40020C00 + 0x00);
	// the base address of GPIOD output data register: `0x40020C00`
	// Address offset: 0x14
	// used to write to the pin
	uint32_t *pGPIODOutputRegister = (uint32_t*) (0x40020C00 + 0x14);

	// step 1. enable the clock for GPIOD peripheral in AHB1ENR
	//	uint32_t temp = *pClockControlRegister;
	//	temp |= 0x08; // modify: only change the 3rd pin as that is the only thing that enables our peripheral
	//	// the info is given in pg 244 reference manual
	//	*pClockControlRegister = temp;
	//
	// step 1. enable the clock for GPIOD peripheral in AHB1ENR
	*pClockControlRegister |= 0x08;   // 00001000 == 0x08  // the third bit mask!
	// CHECK GPIODEN in the SFRs window within the RCC

	// step 2. configure the  GPIOD_MODER to output mode
	// sub step a. CLEAR the 25th and 24th bit
	// setting the 12th pin to 01 should set it to output mode corresponding to p12
	*pGPIODModeRegister &= 0xFCFFFFFF;
	// b. make the 24th bit position as 1 - SET operation

	*pGPIODModeRegister |= 0x01000000;

	// step 3. SET the 12th pin of the GPIO register
	*pGPIODOutputRegister |= 0x1000;


    /* Loop forever */
	for(;;);
}
